import React, { useState } from "react";
import { Spinner, Container } from "reactstrap";
import axios from "axios";

function OAuth({ baseUrl, onSuccess, onError, className, provider }) {
  const [loading, setLoading] = useState(false);

  let providerWindow;
  let baseURL;
  let oAuthProvider = provider ? provider : "google";
  baseURL = !baseUrl
    ? (baseURL =
        process.env.SERVER || "https://lit-savannah-98479.herokuapp.com")
    : baseUrl;
  baseURL =
    baseURL[baseURL.length - 1] === "/"
      ? baseURL.substring(0, baseURL.length - 1)
      : baseURL;

  const checkAuth = async () => {
    try {
      const me = await axios.get(`${baseURL}/auth/me`);
      if (!me.data.errors) {
        if (onSuccess) onSuccess();
      }
    } catch (error) {
      if (onError) onError();
    } finally {
      setLoading(false);
    }
  };

  const messageListener = window.addEventListener(
    "message",
    (event) => {
      if (event.source !== providerWindow) return;
      checkAuth();

      // ...
    },
    false
  );

  const openWindow = () => {
    if (loading) return;
    setLoading(true);
    providerWindow = window.open(
      `${baseURL}/auth/${oAuthProvider}`,
      "_blank",
      "width=800, height=800"
    );
    if (window.focus) providerWindow.focus();
    providerWindow.onbeforeunload = () => {
      checkAuth();
      window.removeEventListener("message", messageListener);
    };
  };

  const chooseImage = () => {
    switch (oAuthProvider) {
      case "facebook":
        return "data:image/png;base64,";
      case "google":
      default:
        return "data:image/png;base64,";
    }
  };

  const capitalizeFirstLetter = (s) => s[0].toUpperCase() + s.slice(1);

  return (
    <Container className={className}>
      <Container className="text-center">
        {loading ? (
          <Spinner />
        ) : (
          <Container className="my-3">
            <img
              src={chooseImage()}
              width="200px"
              alt={`Log in with ${capitalizeFirstLetter(oAuthProvider)}`}
              style={{ cursor: "pointer" }}
              onClick={() => openWindow()}
            />
          </Container>
        )}
      </Container>
    </Container>
  );
}

export default OAuth;
